package `2022`

object Day6 {

  def findFirstUniqueSubstring(length: Int)(input: String): Int =
    isUnique(input.sliding(length), length)

  def findStartOfPacket(input: String): Int =
    findFirstUniqueSubstring(4)(input)

  def findStartOfMessage(input: String): Int =
    findFirstUniqueSubstring(14)(input)

  @scala.annotation.tailrec
  def isUnique(input: Iterator[String], count: Int): Int =
    if input.hasNext then 
      input.next() match
        case unique if unique.toSet.size == unique.length() => count
        case _ => isUnique(input, count + 1)
    else
      0
    

}

@main def day6Run() = {
  val input =""""""

  println(Day6.findStartOfPacket(input))
  println(Day6.findStartOfMessage(input))
}